import { useDispatch, useSelector} from "react-redux";
import { fetchUsers } from "./userSlice";
import {Container, Button, Table, Spinner} from "react-bootstrap";
const UserList = () => {
  const allUser = useSelector((state) => state.users.entities);
  const authState = useSelector((state) => state.auth)
  const dispatch = useDispatch();
  const doFetchUsers = (e) => {
    e.preventDefault();
  }

  console.log('allUser', allUser);

  return (
    <Container>
      <div>
      <Button disabled={!!authState?.isLoginPending} onClick={doFetchUsers} variant="primary" type="submit">
          {!!authState?.isLoginPending ? (
            <Spinner animation="border" role="status">
              <span className="visually-hidden">Loading...</span>
            </Spinner>
          ) : "Submitt"}
        </Button>
      </div>
      <Table striped bordered hover>
        <thead>
        <tr>
          <th>Id</th>
          <th>Title</th>
          <th>Body</th>
        </tr>
        </thead>

        <tbody>
        {allUser.map((user, index) => (
          <tr key={index}>
            <td>{index + 1}</td>
            <td>{user?.title}</td>
            <td>{user?.body}</td>
          </tr>
        ))}
        </tbody>
      </Table>
    </Container>
  )
}

export default UserList;
